import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const DetailPage = () => {
  const { id } = useParams();
  const [details, setDetails] = useState(null);

  useEffect(() => {
    // Fetch the details based on the `id` or retrieve it from a local source
    const fetchDetails = async () => {
      // Example fetch call
      const response = await fetch(`http://localhost:8000/content`);
      const data = await response.json();
      setDetails(data);
    };

    fetchDetails();
  }, [id]);

  if (!details) {
    return <div>Loading...</div>;
  }
console.log('this is details page data',details[id])
  return (
    <div>
     <h3>{details[id].title}</h3>
     <p>{details[id].paragraphs[0].paragraph_text}</p>
    </div>
  );
}

export default DetailPage;
