import React, { useState, useEffect } from 'react';

const TrendingSection = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content", {
          method: "GET",
          headers: { "Cache-Control": "no-cache" },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const jsonData = await response.json();
        if (Array.isArray(jsonData)) {
          setData(jsonData);
        } else {
          console.error("Fetched data is not an array:", jsonData);
        }
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  // Styles defined as objects
  const sectionStyle = {
    backgroundColor: 'transparent',
    color: 'white',
    padding: '16px',
    borderRadius: '8px',
    margin: '0 auto',
    maxWidth: '360px',
    border: '1px solid white'
  };

  const sectionTitleStyle = {
    fontSize: '20px',
    fontWeight: 'bold',
    marginBottom: '16px',
    textAlign: 'left',
    borderBottom: '1px solid white'
  };

  const trendingTopicStyle = { marginBottom: '24px' };

  const tagStyle = {
    backgroundColor: '#43db4d',
    color: 'black',
    borderRadius: '15px',
    padding: '5px 15px',
    marginRight: '8px',
    marginBottom: '8px',
    display: 'inline-block',
  };

  const topicTitleStyle = {
    fontSize: '18px',
    fontWeight: 'bold',
    marginBottom: '8px',
  };

  const topicContentStyle = {
    fontSize: '14px',
    marginBottom: '12px',
  };

  const readTimeStyle = { fontSize: '12px', marginBottom: '12px' };

  const hrStyle = {
    border: '0',
    height: '1px',
    backgroundColor: '#333',
    marginBottom: '24px',
  };

  return (
    <div style={sectionStyle}>
      <h1 style={sectionTitleStyle}>Trending</h1>
      {data.map((topic, index) => (
          <div key={index} style={trendingTopicStyle}>
            <div>
              {topic.tags.map((tag, tagIndex) => (
                <span key={tagIndex} style={tagStyle}>{tag}</span>
              ))}
            </div>
            <h2 style={topicTitleStyle}>{topic.title}</h2>
            <p style={topicContentStyle}>{topic.content}</p>
            <div style={readTimeStyle}>{topic.readTime}</div>
            {index < data.length - 1 && <hr style={hrStyle} />} {/* Don't render <hr> after the last topic */}
          </div>

      ))}
    </div>
  );
};

export default TrendingSection;
