import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";

// Define the styles for the components
const styles = {
  articleContainer: {
    margin: "0 auto",
    padding: "20px",
    maxWidth: "800px",
    backgroundColor: "#f5f5f5",
    color: "#333",
    fontFamily: '"Helvetica Neue", Helvetica, Arial, sans-serif',
  },
  heading: {
    fontSize: "2em",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  paragraph: {
    fontSize: "1em",
    lineHeight: "1.6",
    textAlign: "justify",
    marginBottom: "20px",
  },
  image: {
    maxWidth: "100%",
    height: "auto",
    marginBottom: "20px",
  },
};

// ArticlePage component definition
const ArticlePage = ({ title, titleImage, paragraphs, images }) => {
  // Combine and sort paragraphs and images based on their order
  const contentItems = [...paragraphs, ...images.map(image => ({
    ...image, 
    paragraph_text: '', // Empty string for text since it's an image
    type: 'image'
  }))]
  .sort((a, b) => a.order - b.order);

  return (
    <article style={styles.articleContainer}>
      <h1 style={styles.heading}>{title}</h1>
      {titleImage && (
        <img
          style={styles.image}
          src={titleImage.image_url}
          alt="Title visual representation"
        />
      )}
      {contentItems.map((item, index) => {
        if (item.paragraph_text) {
          return (
            <p key={item._id} style={styles.paragraph}>
              {item.paragraph_text}
            </p>
          );
        } else if (item.type === 'image') {
          return (
            <img
              key={item._id}
              style={styles.image}
              src={item.image_url}
              alt={`Content image ${index + 1}`}
            />
          );
        } else {
          return null;
        }
      })}
    </article>
  );
};

// PropTypes for the ArticlePage component
ArticlePage.propTypes = {
  title: PropTypes.string.isRequired,
  titleImage: PropTypes.shape({
    image_url: PropTypes.string.isRequired,
    _id: PropTypes.string.isRequired,
  }).isRequired,
  paragraphs: PropTypes.arrayOf(
    PropTypes.shape({
      paragraph_text: PropTypes.string.isRequired,
      order: PropTypes.number.isRequired,
      _id: PropTypes.string.isRequired,
    })
  ).isRequired,
  images: PropTypes.arrayOf(
    PropTypes.shape({
      image_url: PropTypes.string.isRequired,
      order: PropTypes.number.isRequired,
      _id: PropTypes.string.isRequired,
    })
  ).isRequired,
};

// ArticlePageRoute component definition
const ArticlePageRoute = () => {
  const [articleData, setArticleData] = useState({
    title: '',
    title_image: { image_url: '', _id: '' },
    paragraphs: [],
    images: []
  });

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content");
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        // Check if the data has the required properties
        if (data.title && data.title_image && Array.isArray(data.paragraphs) && Array.isArray(data.images)) {
          setArticleData(data);
        } else {
          // Handle the case where the data doesn't match the expected structure
          console.error("Data received does not match expected structure:", data);
        }
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  // Conditional rendering to handle loading state
  if (!articleData || articleData.paragraphs.length === 0 || articleData.images.length === 0) {
    return <div>Loading...</div>;
  }

  // Render the ArticlePage component with the fetched data
  return (
    <ArticlePage
      title={articleData.title}
      titleImage={articleData.title_image}
      paragraphs={articleData.paragraphs}
      images={articleData.images}
    />
  );
};

export default ArticlePageRoute;
