import React, {useState,useEffect}from 'react';
import Card from '../Componets/NextPage/NewsCard';
import TrendingSidebar from '../Componets/ Containers/TrendingItem';
import ArticlePage from '../Componets/Other/Articale';

const SecondPage = () => {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  const src = 'https://media.istockphoto.com/id/155394774/photo/media-news-concept.jpg?s=1024x1024&w=is&k=20&c=CM6KvftLQBgHSmPcmIgK4OEXnMd3APOjMGSGB6Srvow='


  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch('http://localhost:8000/articles');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setArticles(data); // Assuming the API returns an array of articles
      } catch (error) {
        console.error('There was a problem with your fetch operation:', error);
      }
    };

    fetchArticles();
  }, []);
console.log('articles',articles)

const articleData = {
  title: 'Sample Article Title',
  content: [
    { type: 'paragraph', text: article.p1 },
    { type: 'image', src: src, alt: 'Image description' },
    { type: 'paragraph', text: article.p2 },
    { type:'image', src: src , alt: 'Another image description' },
],
codeSnippet: 'article.code_Snipt'
};
  return selectedArticle ? (
    <ArticlePage
    title={articleData.title}
    content={articleData.content}
    codeSnippet={articleData.codeSnippet}
  />
  ) : (
    <div className="App" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'flex-start' }}>
      <div className="cards-container" style={{ flexGrow: 1, width: '300px' }}>
        {articles.map((article, index) => (
          <div key={index} onClick={() => setSelectedArticle(article)}>
            <Card
              title={article.title}
              content={article.p1}
              readTime={article.time}
              ImageSrc={article.title_image}
            />
          </div>
        ))}
      </div>
      <div style={{ margin: '1rem', padding: '1rem', marginTop: '0px' }}>
        <TrendingSidebar />
      </div>
    </div>
  );
};

export default SecondPage;
