import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (section) => {
    setExpandedSections(prevState => ({
      ...prevState,
      [section]: !prevState[section]
    }));
  };

  return (
    <div className="container">
      <header className="header">
        <h1>Terms and conditions</h1>
      </header>
      <nav className="navbar">
        <ul>
          <li className="active">Introduction</li>
          <li>Booking conditions</li>
          <li>Cancellation policy</li>
          <li>No-show policy</li>
          <li>Payment policy</li>
          <li>Privacy policy</li>
        </ul>
      </nav>
      <section className="terms-content">
        <article>
          <h2 onClick={() => toggleSection('introduction')}>
            1. Introduction
            <span className="expand-icon">{expandedSections['introduction'] ? '-' : '+'}</span>
          </h2>
          {expandedSections['introduction'] && <p>The following terms and conditions apply to all bookings made by a guest through our website or the hotel directly...</p>}
        </article>
        {/* Repeat for other sections as needed */}
      </section>
    </div>
  );
};

export default App;
