import React, { useState } from 'react';
import './TermsAndConditions.css';

const sections = {
  Introduction: 'The following terms and conditions apply to all bookings made by a guest through our website or the hotel directly...',
  BookingConditions: 'The term "Hotel" refers to the hotel for which you make a booking...',
  // ... other sections
};

function App() {
  const [activeSection, setActiveSection] = useState('Introduction');

  return (
    <div className="terms-container">
      <ul className="tabs">
        {Object.keys(sections).map((key) => (
          <li
            key={key}
            className={`tab-item ${activeSection === key ? 'active' : ''}`}
            onClick={() => setActiveSection(key)}
          >
            {key.replace(/([A-Z])/g, ' $1').trim()}
          </li>
        ))}
      </ul>
      <div className="content">
        <h2>{activeSection.replace(/([A-Z])/g, ' $1').trim()}</h2>
        <p>{sections[activeSection]}</p>
      </div>
    </div>
  );
}

export default App;
