import React from 'react';
import "../../css/MobaileScreenCard.css" // Make sure to create this CSS file

const Card = ({ imageSrc, title, content, time }) => {
  return (
    <div className="card">
      <img src={imageSrc} alt="Card" className="card-image"/>
      <div className="card-content">
        <h1 className="card-title">{title}</h1>
        <p className="card-text">{content}</p>
        <div className="card-footer">
          <span className="time">{time}</span>
        </div>
      </div>
    </div>
  );
};

const NewsMobaileCard = () => {
  const [news, setNews] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content", {
          method: "GET",
          headers: {
            "Cache-Control": "no-cache",
          },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNews(data);
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  console.log("this is news", news);
  const cards = [
    {
      "id": 1,
      "imageSrc": "https://via.placeholder.com/150",
      "title": "Card Title 1",
      "content": "This is the content of the first card. It has a brief description or summary.",
      "time": "2 mins ago"
    },
    {
      "id": 2,
      "imageSrc": "https://via.placeholder.com/150",
      "title": "Card Title 2",
      "content": "This is the content of the second card. It includes some more detailed information.",
      "time": "5 mins ago"
    },
    {
      "id": 3,
      "imageSrc": "https://via.placeholder.com/150",
      "title": "Card Title 3",
      "content": "Content for the third card can be more descriptive and might include various details.",
      "time": "10 mins ago"
    }
  ]

    return (
      <div className="card-container">
        {cards.map((card, index) => (
          <Card key={index} {...card} />
        ))}
      </div>
    );
  }

  export default NewsMobaileCard;
