import React from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #1a1a1a;
  color: white;
  padding: 20px;
  margin: 10px;
  border-radius: 10px;
  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
  width: 300px; // Adjust width as necessary
`;

const Tab = styled.div`
  display: inline-block;
  background-color: ${props => props.active ? '#333' : 'transparent'};
  padding: 10px;
  border-radius: 10px 10px 0 0;
  margin-right: 5px;
  cursor: pointer;
`;

const Title = styled.h1`
  font-size: 18px;
  font-weight: bold;
`;

const Text = styled.p`
  font-size: 14px;
`;

const Card = ({ title, content }) => {
  const [activeTab, setActiveTab] = React.useState('topic1');

  return (
    <CardContainer>
      <div>
        <Tab active={activeTab === 'topic1'} onClick={() => setActiveTab('topic1')}>Topic 1</Tab>
        <Tab active={activeTab === 'topic2'} onClick={() => setActiveTab('topic2')}>Topic 2</Tab>
      </div>
      <Title>{title}</Title>
      <Text>{content}</Text>
    </CardContainer>
  );
};



const ArticleMobaileScreen = () => {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', overflowX: 'scroll' }}>
      <Card title="Lorem ipsum dolor sit amet" content="Lorem ipsum dolor sit amet consectetur adipiscing elit..." />
      <Card title="Sed do eiusmod tempor" content="Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua..." />
      // Add more cards as needed
    </div>
  );
}

export default ArticleMobaileScreen;
