import React from 'react';
import styled from 'styled-components';

const TrendingItem = ({ title, description, readTime }) => {
  return (
    <div style={{
      borderBottom: '1px solid #ffffff26', // light border for separator
      paddingBottom: '1rem',
      marginBottom: '1rem',
    }}>
      <h2 style={{ color: 'white', fontSize: '1.2rem' }}>{title}</h2>
      <p style={{ color: 'white', fontSize: '0.9rem' }}>{description}</p>
      <p style={{ color: 'white', fontSize: '0.8rem' }}>{readTime}</p>
    </div>
  );
};




// Styled components
const TrendingCard = styled.div`
  border: 1px solid white;
  color: white;
  padding: 20px;
  border-radius: 10px;

  font-family: 'Arial', sans-serif;
`;

const Title = styled.h2`
  font-size: 1.5em;
  border-bottom: 1px solid grey;
  padding-bottom: 10px;
  margin-bottom: 20px;
`;

const TrendingListItem = styled.p`
  font-size: 1em;
  margin-bottom: 15px;
  &:last-child {
    margin-bottom: 0;
  }
`;

const TrendingList = styled.div`
  font-size: 0.9em;
`;

// React component
const Trending = () => {
  // Dummy data
  const trends = [
    'Lorem ipsum dolor sit amet consectetur.',
    'Velit condimentum nibh erat et. Volutpat.',
    'Neque risus iaculis aenean eget ut ultrices.',
    'Congue pharetra amet molestie a in. Mauris.',
    'Lorem ipsum dolor sit amet consectetur.'
    // ... you can add more items here
  ];

  return (
    <TrendingCard>
      <Title>Trending</Title>
      <TrendingList>
        {trends.map((item, index) => (
          <TrendingListItem key={index}>{item}</TrendingListItem>
        ))}
      </TrendingList>
    </TrendingCard>
  );
};



const TrendingSidebar = () => {
  // Sample data
  const trendingData = [
    {
      title: "Lorem ipsum dolor sit amet.",
      description: "Lorem ipsum dolor sit amet consectetur. Velit condimentum nibh erat et. Voluptat.",
      readTime: "2 mins read"
    },
    {
      title: "Lorem ipsum dolor sit amet.",
      description: "Lorem ipsum dolor sit amet consectetur. Velit condimentum nibh erat et. Voluptat.",
      readTime: "2 mins read"
    },

    // ... more items
  ];

  return (
    <div>
      <Trending />
    <div style={{
      color: '#FFFFFF', // white text
      width: '300px', // fixed width for the sidebar
      padding: '1rem',
      boxSizing: 'border-box',
      height: '100vh', // full height
      overflowY: 'auto' // in case of scrolling
    }}>
      {trendingData.map((item, index) => (
        <TrendingItem 
          key={index}
          title={item.title}
          description={item.description}
          readTime={item.readTime}
        />
      ))}
    </div>
    </div>
  );
};

export default TrendingSidebar;
