import React from 'react';
import './App.css';

// Dummy data for trending topics
const trendingTopics = [
  {
    title: 'Lorem ipsum dolor sit amet consectetur.',
    content: 'Lorem ipsum dolor sit amet consectetur, elit. Voluptat...',
    readTime: '2 mins read'
  },
  // ... more topics
];

// Dummy data for main content
const mainContent = {
  topicOne: [
    {
      title: 'Lorem ipsum dolor sit amet.',
      content: 'Lorem ipsum dolor sit amet consectetur, Nam magna ...',
      readTime: '2 mins read'
    },
    // ... more topics
  ],
  topicTwo: [
    // ... topics
  ]
};

// Trending topics component
const TrendingTopics = ({ topics }) => {
  return (
    <div className="trending-container">
      <h2>ðŸ”¥ Trending</h2>
      {topics.map((topic, index) => (
        <Topic key={index} title={topic.title} content={topic.content} readTime={topic.readTime} />
      ))}
    </div>
  );
};

// Topic component
const Topic = ({ title, content, readTime }) => {
  return (
    <div className="topic">
      <h3>{title}</h3>
      <p>{content}</p>
      <span>{readTime}</span>
    </div>
  );
};

// Main content component
const MainContent = ({ content }) => {
  return (
    <div className="main-content">
      <section>
        <h2>Topic One</h2>
        {content.topicOne.map((topic, index) => (
          <Topic key={index} title={topic.title} content={topic.content} readTime={topic.readTime} />
        ))}
      </section>
      <section>
        <h2>Topic Two</h2>
        {content.topicTwo.map((topic, index) => (
          <Topic key={index} title={topic.title} content={topic.content} readTime={topic.readTime} />
        ))}
      </section>
    </div>
  );
};

// App component
const App = () => {
  return (
    <div className="app">
      <TrendingTopics topics={trendingTopics} />
      <MainContent content={mainContent} />
    </div>
  );
};

export default App;
