import React, { useState } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: transparent;
  color: white;
  padding: 20px;
  margin: 10px;
  border-radius: 10px;
  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
  width: 330px;
`;

const Tab = styled.div`
  display: inline-block;
  background-color: ${props => props.active ? 'green' : 'transparent'};
  padding: 10px;
  border-radius: 10px 10px 0 0;
  margin-right: 5px;
  cursor: pointer;
  margin-top: 15px;
`;

const Title = styled.h1`
  font-size: 18px;
  font-weight: bold;
`;

const Text = styled.p`
  font-size: 14px;
`;

const Card = ({ title, content, onClick }) => {
  const [activeTab, setActiveTab] = React.useState('topic1');
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch('http://localhost:8000/articles');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setArticles(data);
      } catch (error) {
        console.error('There was a problem with your fetch operation:', error);
      }
    };

    fetchArticles();
  }, []);


console.log(articles)

  return (
    <CardContainer onClick={onClick}>
      <div style={{height:'200px',backgroundColor:'whitesmoke',borderRadius:'5px'}}></div>
      <div>
        <Tab active={activeTab === 'topic1'} onClick={() => setActiveTab('topic1')}>Topic 1</Tab>
        <Tab active={activeTab === 'topic2'} onClick={() => setActiveTab('topic2')}>Topic 2</Tab>
      </div>
      <Title>{title}</Title>
      <Text>{content}</Text>
    </CardContainer>
  );
};

const ArticleMobaileScreen = () => {
  const [activeCard, setActiveCard] = useState(null);

  const cards = [
    { title: "Lorem ipsum dolor sit amet", content: "Lorem ipsum dolor sit amet consectetur adipiscing elit..." },
    { title: "Sed do eiusmod tempor", content: "Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua..." },
    // Add more cards as needed
  ];

  const renderCard = (card, index) => (
    <Card
      key={index}
      title={card.title}
      content={card.content}
      onClick={() => setActiveCard(card)}
    />
  );

  const renderActiveCard = () => (
    <div style={{ padding: '20px', color: 'white' }}>
      <h1>{activeCard.title}</h1>
      <p>{activeCard.content}</p>
      {/* Here, you can add more details or actions related to the active card */}
    </div>
  );

  return (
    <div style={{ display: 'flex', flexDirection: 'column', justifyItems: 'center', overflowX: 'scroll' }}>
      {activeCard == null ? cards.map(renderCard) : renderActiveCard()}
    </div>
  );
};

export default ArticleMobaileScreen;
