// Import React, useEffect, useState, Suspense for lazy loading
import React, { useEffect, useState, Suspense } from "react";
import { Route, BrowserRouter, Routes } from "react-router-dom";
import Header from "./components/Header";
import ProtectedRoute from "./ProtectedRoute";
import { getCookie } from "./utils/methods";
import { getCredits } from "./services/auth";
import Alert from "./components/Alert";
import Featured from "./pages/Featured";
import Subscription from "./pages/Subscription";
import "./App.css";
import { RingLoader } from "react-spinners";

// Lazy load the components
const ModelGeneration = React.lazy(() => import("./pages/model-generation"));
const Login = React.lazy(() => import("./pages/login"));
const Register = React.lazy(() => import("./pages/register"));
const ChangePassword = React.lazy(() => import("./pages/change-password"));
const Collection = React.lazy(() => import("./pages/collection"));
const Model = React.lazy(() => import("./pages/model"));
const Upgrade = React.lazy(() => import("./components/Upgrade"));
const NotFound = React.lazy(() => import("./pages/404"));
const DashBoard = React.lazy(() => import("./pages/DashBoard"));
const HomePage = React.lazy(() => import("./pages/HomePage"));
const ChatNew = React.lazy(() => import("./pages/ChatNew"));
const VoiceAssi = React.lazy(() => import("./pages/TTSpage"));
const Survey = React.lazy(() => import("./surveyQuestions/main"));
const Main = React.lazy(() => import("./account/AccountMain"));
const HistoryStorage = React.lazy(
  () => import("./History_Components/History_main")
);
const Main_chatbot = React.lazy(() => import("./chatbot/Main_chatbot"));
// const Social_main = React.lazy(
//   () => import("../src/socialFeature/social_main")
// );

function App() {
  const [isAuth, setIsAuth] = useState(getCookie("token") ? true : false);
  // const [userCredits, setUserCredits] = useState(null);\
  const [userCredits, setUserCredits] = useState(() => {
    const storedCredits = localStorage.getItem("userCredits");
    return storedCredits ? parseInt(storedCredits, 10) : null;
  });

  const [errorMessage, setErrorMessage] = useState("");
  const [sessionChats, setSessionChats] = useState(null);
  const [sessionIds, setSessionIds] = useState(null);

  const handleSessionClicks = (sessionChats, sessionIds) => {
    setSessionChats(sessionChats);
    setSessionIds(sessionIds); // Set sessionIds when sessionChats are updated
  };

  const getUserCredits = async () => {
    const response = await getCredits();
    if (response && response.error) {
      setErrorMessage(response.error);
      return;
    } else {
      setErrorMessage("");
    }
    if (response.data) {
      console.log("cre response.data", response.data);
      setUserCredits(response.data);
      // Added the localstorage
      localStorage.setItem("userCredits", response.data);
    }
  };

  useEffect(() => {
    if (isAuth) {
      getUserCredits();
    }
  }, [isAuth]);

  useEffect(() => {
    console.log("getCookie('token')", getCookie("token"));
    if (getCookie("token")) {
      setIsAuth(true);
    }
  }, []);

  useEffect(() => {
    if (
      !window.location.pathname.includes("/login") &&
      !window.location.pathname.includes("/register")
    ) {
      getUserCredits();
    }
    // console.log("Changed credits", userCredits);
  }, [userCredits]);

  // Check if the current route is /subscription
  const isSubscriptionRoute =
    window.location.pathname.includes("/subscription");

  return (
    <div className="App">
      {errorMessage ? <Alert message={errorMessage} /> : null}
      <BrowserRouter>
        <Header
          isAuth={isAuth}
          userCredits={userCredits}
          setIsAuth={setIsAuth}
        />

        <Suspense
          fallback={
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "75vh",
              }}
            >
              <RingLoader color="#6cd97e" size={100} />
            </div>
          }
        >
          {" "}
          <Routes>
            <Route path="/HomePage" element={<HomePage />}>
              <Route path="/HomePage" element={<HomePage />} />
            </Route>
          </Routes>
        </Suspense>
      </BrowserRouter>
    </div>
  );
}

export default App;
