import React from 'react';

// The main contact component
const ContactUs = () => {
  return (
    <div className="contact-us-container">
      <SectionHeader />
      <ReservationCentre />
      <HotelBusinessCentre />
      <MediaAndPress />
      <FeedbackSection />
      <FollowUs />
      <FAQ />
      <PostalInfo />
    </div>
  );
};

// Header for the section
const SectionHeader = () => {
  return (
    <div className="section-header">
      <h2>Contact us</h2>
      <p>We are here to help! MyTravelway has extended support and communication round the clock on business days. You may reach out to us at your convenience. We keep a record of all the communication for quality, training, and research purposes and to serve you in a better way. Following is the contact information to address your queries. Business Days: Monday to Saturday, Timing: 8:00AM to 11:00PM, Sunday- Week-off and Holidays</p>
    </div>
  );
};

// Reservation Centre section
const ReservationCentre = () => {
  return (
    <div className="reservation-centre">
      <h3>Reservations Centre</h3>
      <p>Customer service hotline: +1 (800) 123-4567</p>
      <p>Email: info@splendid.com</p>
    </div>
  );
};

// Hotel Business Centre section
const HotelBusinessCentre = () => {
  return (
    <div className="hotel-business-centre">
      <h3>Hotel Business Centre</h3>
      <p>Partnership inquiries: partnerships@splendid.com</p>
    </div>
  );
};

// Media and Press section
const MediaAndPress = () => {
  return (
    <div className="media-and-press">
      <h3>Media and press</h3>
      <p>Media inquiries: press@splendid.com</p>
    </div>
  );
};

// Feedback section
const FeedbackSection = () => {
  return (
    <div className="feedback-section">
      <h3>Feedback</h3>
      <p>Share your feedback: feedback@splendid.com</p>
    </div>
  );
};

// Follow Us section
const FollowUs = () => {
  return (
    <div className="follow-us">
      <h3>Follow us</h3>
      {/* Social media icons or links would go here */}
    </div>
  );
};

// FAQ section
const FAQ = () => {
  return (
    <div className="faq-section">
      <h3>FAQ</h3>
      {/* Link to FAQ page */}
    </div>
  );
};

// Postal/Contact Information
const PostalInfo = () => {
  return (
    <div className="postal-info">
      <h3>Postal/Mail</h3>
      <p>171, Panduranga Nagar, Off. B.G. Road, Bengaluru-560076</p>
    </div>
  );
};

export default ContactUs;
