import React,{useState,useEffect} from 'react';
import '../../css/NewsCard.css'; // Ensure the CSS file is linked

const NewsCard = ({ title, content, readTime }) => {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch('http://localhost:8000/articles');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setArticles(data); // Assuming the API returns an array of articles
      } catch (error) {
        console.error('There was a problem with your fetch operation:', error);
      }
    };

    fetchArticles();
    console.log(articles);
  }, []);

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <span className="tag">Topic 1</span>
          <span className="tag">Topic 2</span>
        </div>
        <h2>{title}</h2>
        <p>{content}</p>
        <div className="card-footer">
          <span>{readTime} mins read</span>
        </div>
      </div>
      <div className="side-div">
        {/* Content for the side div goes here */}
      </div>
    </div>
  );
};

export default NewsCard;
