import React from 'react';
import './App.css';

// Component for displaying each paragraph
const Paragraph = ({ text }) => {
  return <p className="paragraph">{text}</p>;
};

// Component for displaying each image
const ImageComponent = ({ src }) => {
  return (
    <div className="image-container">
      <img src={src} alt="Content" className="content-image" />
    </div>
  );
};

// Main App component
const App = ({ data }) => {
  return (
    <div className="app-container">
      <h1 className="title">{data.title}</h1>
      <div className="title-image-container">
        <img src={data.title_image.image_url} alt="Title" className="title-image" />
      </div>
      <div className="paragraphs-container">
        {data.paragraphs
          .sort((a, b) => a.order - b.order)
          .map((para, index) => (
            <Paragraph key={index} text={para.paragraph_text} />
          ))}
      </div>
      <div className="images-container">
        {data.images.map((img, index) => (
          <ImageComponent key={index} src={img.image_url} />
        ))}
      </div>
    </div>
  );
};

export default App;
