import React,{useState,useEffect} from 'react';
import '../css/homePage.css'; // Make sure this path includes both CSS if they are separate or ensure the combined CSS is correctly referenced
import LeftPanel from '../Componets/SideBar';
import CardContainer from '../Componets/CardContainer';
import ImageCardContainer from '../Componets/ImageCardContainer';
import TrendingSidebar from '../Componets/ Containers/TrendingItem';
import Image from '../assets/Images/FeaturedModels/block-5.png'
import ArticlePage from "../Componets/Other/NewsAriticalPage"

function App() {
const [news, setNews] = useState([]);

useEffect(() => {
  const fetchNews = async () => {
    try {
      const response = await fetch('http://localhost:8000/content', {
        method: 'GET',
        headers: {
          'Cache-Control': 'no-cache', // This tells the browser to fetch a fresh copy
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setNews(data); // Assuming the API returns an array of articles
    } catch (error) {
      console.error('There was a problem with your fetch operation:', error);
    }
  };

  fetchNews();
}, []); 


console.log('this is news', news);



  const articleData = {
    title: 'Sample Article Title',
    content: [
      { type: 'paragraph', text: 'This is the first paragraph of the article.' },
      { type: 'image', src: 'path_to_image_1.jpg', alt: 'Image description' },
      { type: 'paragraph', text: 'This is the second paragraph of the article.' },
      { type: 'image', src: 'path_to_image_2.jpg', alt: 'Another image description' },
      // ... additional paragraphs and images
    ]
  };




  console.log('newdata',news)
  function Section({ news }) {
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleNext = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % news.length);
    };

    const handlePrev = () => {
      setCurrentIndex((prevIndex) => (prevIndex - 1 + news.length) % news.length);
    };

    // Calculate the articles to display based on currentIndex
    const displayedArticles = news.slice(currentIndex, currentIndex + 3);

    return (
      <div className="section-container">
        <h2 className="section-header">News Section</h2>
        <div className="articles-container">
          {displayedArticles.map((article, index) => (
            <Article
              key={index}
              title={article.title}
              paragraph_text={article.paragraph_text}
              time={article.time}
            />
          ))}
        </div>
        <button onClick={handlePrev} disabled={currentIndex === 0}>Prev</button>
        <button onClick={handleNext} disabled={currentIndex + 3 >= news.length}>Next</button>
      </div>
    );
  }


function Article({ title, paragraph_text, time }) {
  console.log('newdata',news)
  return (
    <div className="article-card">
      <img src={Image} alt="Article Thumbnail" className="article-image" />
      <div className="article-content">
        <h3>{title}</h3>
        <p>{paragraph_text}</p>
        <div className="read-time">{time} mins read</div>
      </div>
    </div>
  );
}
  return (
    <div className="layout-container">
    <aside className="left-panel">
      <LeftPanel />
    </aside>
    <main className="main-content">
     <div style={{ display: 'flex', flexDirection: 'row'}}>
    {/* Sidebar */}
    <div style={{
      minWidth: '300px', // Adjust based on your preference
      background: 'transparent',
      height:'680px' // Light gray background for contrast
    }}>
      <TrendingSidebar />
    </div>

    {/* Main Content */}
    <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
      {/* CardContainer */}
      <div style={{
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'stretch',
        width: '100%',
        margin: '0 auto',
        boxSizing: 'border-box',
      }}>
       <CardContainer  />
      </div>

      {/* ImageCardContainer */}
      <div style={{
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'stretch',
        width: '100%',
        margin: '0 auto',
        boxSizing: 'border-box',
      }}>
       <ImageCardContainer />
      </div>
    </div>
  </div>
      <Section title="Topic One" />
      <Section title="Topic Two" />
      {/* Add more sections as needed */}
      <div>
      <ArticlePage title={articleData.title} content={articleData.content} />
    </div>
    </main>
    {/* <footer className="app-footer">
      Footer Content Here
    </footer> */}
  </div>

  );
}


export default App;
