import React from 'react';
import PropTypes from 'prop-types';

const ArticlePage = ({ title, content }) => {
  return (
    <article style={styles.articleContainer}>
      {title && <h1 style={styles.heading}>{title}</h1>}

      {content.map((item, index) => {
        if (item.type === 'paragraph') {
          return (
            <p key={index} style={styles.paragraph}>
              {item.text}
            </p>
          );
        } else if (item.type === 'image') {
          return (
            <img key={index} style={styles.image} src={item.src} alt={item.alt} />
          );
        } else {
          return null;
        }
      })}
    </article>
  );
};

const styles = {
  articleContainer: {
    margin: '0 auto',
    padding: '20px',
    color: '#ffffff',
    fontFamily: '"Helvetica Neue", Helvetica, Arial, sans-serif',
  },
  heading: {
    fontSize: '2em',
    fontWeight: 'bold',
    marginBottom: '20px',
  },
  paragraph: {
    fontSize: '1em',
    lineHeight: '1.6',
    textAlign: 'justify',
    marginBottom: '20px',
  },
  image: {
    maxWidth: '100%',
    height: 'auto',
    marginBottom: '20px',
  },
};

ArticlePage.propTypes = {
  title: PropTypes.string.isRequired,
  content: PropTypes.arrayOf(
    PropTypes.shape({
      type: PropTypes.oneOf(['paragraph', 'image']).isRequired,
      text: PropTypes.string,
      src: PropTypes.string,
      alt: PropTypes.string,
    })
  ).isRequired,
};

// Simple function component
const ArticlePageRoute = () => {
  const articleData = {
    title: 'Sample Article Title',
    content: [
      {
        type: 'paragraph',
        text: 'This is the first paragraph of the article.',
      },
      { type: 'image', src: 'path_to_image_1.jpg', alt: 'Image description' },
      {
        type: 'paragraph',
        text: 'This is the second paragraph of the article.',
      },
      {
        type: 'image',
        src: 'path_to_image_2.jpg',
        alt: 'Another image description',
      },
    ],
  };

  const [news, setNews] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content", {
          method: "GET",
          headers: {
            "Cache-Control": "no-cache", 
          },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNews(data); 
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  console.log("this is news", news);

  return (
    <>
      <ArticlePage title={articleData.title} content={articleData.content} />
      <h1>Hello</h1>
    </>
  );
};

export default ArticlePageRoute;
