import React, { useState } from 'react';
import './App.css';

const sections = {
  'Introduction': 'The following terms and conditions apply to all bookings made by a guest through our website or the hotel directly...',
  'Booking Conditions': '...',
  'Cancellation Policy': '...',
  'No-Show Policy': '...',
  'Payment Policy': '...',
  'Privacy Policy': '...'
};

const App = () => {
  const [activeSection, setActiveSection] = useState('Introduction');

  return (
    <div className="terms-container">
      <div className="tabs">
        {Object.keys(sections).map((section) => (
          <div
            key={section}
            className={`tab ${activeSection === section ? 'active' : ''}`}
            onClick={() => setActiveSection(section)}
          >
            {section}
          </div>
        ))}
      </div>
      <div className="content">
        {Object.keys(sections).map((section) => (
          <div
            key={section}
            className={`content-section ${activeSection === section ? 'active' : 'hidden'}`}
          >
            <h2>{section}</h2>
            <p>{sections[section]}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
