import React from 'react';

const TrendingSection = () => {
  // Dummy data for topics
  const topics = [
    {
      title: 'Lorem ipsum dolor sit amet.',
      content: 'Lorem ipsum dolor sit amet consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.'
    },
    {
      title: 'Ut enim ad minim veniam.',
      content: 'Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.'
    },
    {
      title: 'Duis aute irure dolor in reprehenderit.',
      content: 'Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.'
    },
    {
      title: 'Excepteur sint occaecat cupidatat.',
      content: 'Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'
    }
  ];

  // Inline styles
  const sectionStyle = {
    backgroundColor: '#1a1a1a',
    color: '#fff',
    padding: '20px',
    borderRadius: '8px',
    fontFamily: 'Arial, sans-serif'
  };

  const sectionTitleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'left',
    borderBottom: '1px solid #333',
    paddingBottom: '10px'
  };

  const topicStyle = {
    marginBottom: '20px',
    borderBottom: '1px solid #333',
    paddingBottom: '10px'
  };

  const tagStyle = {
    backgroundColor: '#4CAF50', // Green background for tags
    borderRadius: '5px',
    padding: '5px 10px',
    marginRight: '5px',
    marginBottom: '10px',
    display: 'inline-block',
    color: 'white'
  };

  const titleStyle = {
    fontSize: '18px',
    fontWeight: 'bold',
    marginBottom: '8px'
  };

  const contentStyle = {
    fontSize: '14px',
    marginBottom: '10px'
  };

  const readTimeStyle = {
    fontSize: '12px',
    fontStyle: 'italic'
  };

  return (
    <div style={sectionStyle}>
      <h1 style={sectionTitleStyle}>Trending</h1>
      {topics.map((topic, index) => (
        <div key={index} style={topicStyle}>
          <div>
            <span style={tagStyle}>Topic 1</span>
            <span style={tagStyle}>Topic 2</span>
          </div>
          <h2 style={titleStyle}>{topic.title}</h2>
          <p style={contentStyle}>{topic.content}</p>
          <div style={readTimeStyle}>2 mins read</div>
        </div>
      ))}
    </div>
  );
};

export default TrendingSection;
