import React from 'react';

const CardContainer = () => {


const TrendingSidebar = () => {
  // Sample data
  const trendingData = [
    {
      title: "Exploring the Wonders of the Natural World",
      description: "Join us on a journey through some of the planet's most breathtaking landscapes"
    },
    {
      title: "The Future of Renewable Energy: Innovations That Could Change the World",
      description: "As the global community seeks sustainable solutions to combat climate change, advancements in renewable energy technology offer hope.",
      readTime: "7 mins read"
    },
  ];


  return (
    <div>
      <Trending />
    <div style={{
      color: '#FFFFFF', // white text
      width: '300px', // fixed width for the sidebar
      padding: '1rem',
      boxSizing: 'border-box',
      height: '100vh', // full height
      overflowY: 'auto' // in case of scrolling
    }}>
      {trendingData.map((item, index) => (
        <TrendingItem 
          key={index}
          title={item.title}
          description={item.description}
          readTime={item.readTime}
        />
      ))}
    </div>
    </div>
  );
};
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'stretch',
      width: '100%',
      margin: '0 auto',
      boxSizing: 'border-box',
      marginLeft:'10px'
    }}>
      {/* First inner div with less width */}
      <div style={{
        width: '35%', // Smaller width
        height:'300px',
        backgroundColor: 'lightgray', // Just for visualization
        borderRadius:'10px',
      }}>
        First Div
      </div>

      {/* Second inner div with more width */}
      <div style={{
        width: '63%', // Larger width
        backgroundColor: 'darkgray', // Just for visualization
        borderRadius:'10px'
      }}>
        Second Div
      </div>
    </div>
  );
};

export default CardContainer;
