import React from 'react';

const tagStyle = {
  backgroundColor: '#444', // Darker background for the tags
  color: 'white',
  borderRadius: '5px',
  padding: '5px 10px',
  display: 'inline-block', // Use inline-block for tag-like appearance
  marginRight: '8px', // Space between tags
};

const trendingTopicStyle = {
  marginBottom: '20px',
};

const topicTitleStyle = {
  fontSize: '18px',
  marginBottom: '10px',
};

const topicContentStyle = {
  fontSize: '14px',
  marginBottom: '10px',
};

const readTimeStyle = {
  fontSize: '12px',
  marginBottom: '10px',
};

const hrStyle = {
  border: '0',
  height: '1px',
  backgroundColor: '#333', // Slightly lighter line for separation
  marginBottom: '20px',
};

const TrendingTopic = ({ title, content }) => {
  return (
    <div style={trendingTopicStyle}>
      <div>
        <span style={tagStyle}>Topic 1</span>
        <span style={tagStyle}>Topic 2</span>
      </div>
      <h2 style={topicTitleStyle}>{title}</h2>
      <p style={topicContentStyle}>{content}</p>
      <div style={readTimeStyle}>2 mins read</div>
      <hr style={hrStyle} />
    </div>
  );
};

const TrendingSection = () => {
  const topics = [
    // ... your topics
  ];

  const sectionStyle = {
    backgroundColor: '#000', // Assuming dark theme from the screenshot
    color: '#fff',
    padding: '20px',
    borderRadius: '8px',
  };

  const sectionTitleStyle = {
    fontSize: '24px',
    marginBottom: '20px',
  };

  return (
    <div style={sectionStyle}>
      <h1 style={sectionTitleStyle}>Trending</h1>
      {topics.map((topic, index) => (
        <TrendingTopic key={index} title={topic.title} content={topic.content} />
      ))}
    </div>
  );
};

export default TrendingSection;
