// Import React, useEffect, useState, Suspense for lazy loading
import React, { Suspense } from "react";
import { Route, BrowserRouter, Routes } from "react-router-dom";
import Header from "../src/Componets/Header";
import "./App.css";
// Ensure you have react-spinners installed to use RingLoader
// npm install react-spinners
// import { RingLoader } from "react-spinners";

// Lazy load the components
const HomePage = React.lazy(() => import("./Pages/HomePage"));

function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <Header
          isAuth={''}
          userCredits={''}
          setIsAuth={''}
        />

        <Suspense
          fallback={
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "75vh",
              }}
            >
              {/* Uncomment below to use RingLoader if you have react-spinners installed */}
              {/* <RingLoader color="#6cd97e" size={100} /> */}
              {/* If not using react-spinners, you can keep the simple text loading. */}
              loading ....
            </div>
          }
        >
          <Routes>
            {/* Corrected Route setup to not nest HomePage within itself */}
            <Route path="/HomePage" element={<HomePage />} />
            {/* If you want the HomePage to be your landing page, consider adding a redirect or setting the path to "/" */}
            {/* <Route path="/" element={<HomePage />} /> */}
          </Routes>
        </Suspense>
      </BrowserRouter>
    </div>
  );
}

export default App;
