import React from 'react';

// Main contact component
const App = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '20px', backgroundColor: '#f2f2f2', maxWidth: '600px', margin: 'auto' }}>
      <SectionHeader />
      <ContactItem 
        title="Reservations Centre"
        items={[
          { type: 'phone', value: '+1 (800) 123-4567' },
          { type: 'email', value: 'info@splendid.com' }
        ]}
      />
      <ContactItem 
        title="Hotel Business Centre"
        items={[
          { type: 'email', value: 'partnerships@splendid.com' }
        ]}
      />
      <ContactItem 
        title="Media and press"
        items={[
          { type: 'email', value: 'press@splendid.com' }
        ]}
      />
      <ContactItem 
        title="Feedback"
        items={[
          { type: 'email', value: 'feedback@splendid.com' }
        ]}
      />
      <FollowUs />
      <FAQ />
      <PostalInfo />
    </div>
  );
};

// Section Header
const SectionHeader = () => {
  return (
    <div style={{ marginBottom: '30px' }}>
      <h2 style={{ color: '#333' }}>Contact us</h2>
      <p style={{ fontSize: '14px', color: '#666' }}>We are here to help! MyTravelway has extended support and communication round the clock on business days. You may reach out to us at your convenience. We keep a record of all the communication for quality, training, and research purposes and to serve you in a better way. Following is the contact information to address your queries. Business Days: Monday to Saturday, Timing: 8:00AM to 11:00PM, Sunday- Week-off and Holidays</p>
    </div>
  );
};

// Contact item component for phone and email
const ContactItem = ({ title, items }) => {
  return (
    <div style={{ marginBottom: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '10px' }}>{title}</h3>
      {items.map(item => (
        <p key={item.value} style={{ fontSize: '14px', color: '#666' }}>
          {item.type === 'phone' ? 'Customer service hotline: ' : ''}
          {item.value}
        </p>
      ))}
    </div>
  );
};

// Follow Us section
const FollowUs = () => {
  return (
    <div style={{ marginBottom: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '10px' }}>Follow us</h3>
      {/* Icons or links for social media would go here */}
    </div>
  );
};

// FAQ section
const FAQ = () => {
  return (
    <div style={{ marginBottom: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '10px' }}>FAQ</h3>
      {/* The arrow or link to the FAQ page would go here */}
    </div>
  );
};

// Postal/Contact Information
const PostalInfo = () => {
  return (
    <div style={{ marginBottom: '20px' }}>
      <h3 style={{ color: '#333', marginBottom: '10px' }}>Postal/Mail</h3>
      <p style={{ fontSize: '14px', color: '#666' }}>171, Panduranga Nagar, Off. B.G. Road, Bengaluru-560076</p>
    </div>
  );
};

export default App;
