import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Prism from 'prismjs';
import 'prismjs/themes/prism-okaidia.css'; // Theme resembling VS Code
import 'prismjs/components/prism-javascript';

const ArticlePage = ({ title, content = [], codeSnippet }) => {
    useEffect(() => {
        Prism.highlightAll();
    }, []);

    // Ensure content is an array before mapping
    const safeContent = Array.isArray(content) ? content : [];

    return (
        <article style={styles.articleContainer}>
            {title && <h1 style={styles.heading}>{title}</h1>}
            {safeContent.map((item, index) => {
                switch (item.type) {
                    case 'paragraph':
                        return <p key={index} style={styles.paragraph}>{item.text}</p>;
                    case 'image':
                        return <img key={index} style={styles.image} src={item.src} alt={item.alt} />;
                    default:
                        return null;
                }
            })}
            {codeSnippet && (
                <pre style={styles.codeBlock}>
                    <code className="language-js">
                        {codeSnippet}
                    </code>
                </pre>
            )}
        </article>
    );
};

const styles = {
    articleContainer: {
        margin: '0 auto',
        padding: '20px',
        color: '#ffffff',
        fontFamily: '"Helvetica Neue", Helvetica, Arial, sans-serif',
    },
    heading: {
        fontSize: '2em',
        fontWeight: 'bold',
        marginBottom: '20px',
    },
    paragraph: {
        fontSize: '1em',
        lineHeight: '1.6',
        textAlign: 'justify',
        marginBottom: '20px',
    },
    image: {
        maxWidth: '100%',
        height: 'auto',
        marginBottom: '20px',
    },
    codeBlock: {
        background: 'black',
        border: '1px solid #ccc',
        borderRadius: '4px',
        padding: '10px',
        overflowX: 'auto',
        fontFamily: '"Courier New", Courier, monospace',
        fontSize: '0.9em',
        lineHeight: '1.4',
        margin: '20px 0',
    }
};

ArticlePage.propTypes = {
    title: PropTypes.string.isRequired,
    content: PropTypes.arrayOf(
        PropTypes.shape({
            type: PropTypes.oneOf(['paragraph', 'image']).isRequired,
            text: PropTypes.string,
            src: PropTypes.string,
            alt: PropTypes.string
        })
    ).isRequired,
    codeSnippet: PropTypes.string,
};

export default ArticlePage;
