import React, { useState, useEffect } from 'react';
import Card from '../Components/NextPage/NewsCard';
import TrendingSidebar from '../Components/ Containers/TrendingItem';
import ArticlePage from '../Components/Other/Article';

const SecondPage = () => {
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch('http://localhost:8000/articles');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setArticles(data); // Assuming the API returns an array of articles
      } catch (error) {
        console.error('There was a problem with your fetch operation:', error);
      }
    };

    fetchArticles();
  }, []);

  if (selectedArticle) {
    return (
      <div>
        <ArticlePage
          title={selectedArticle.title}
          content={selectedArticle.content}
          codeSnippet={selectedArticle.codeSnippet}
        />
      </div>
    );
  }

  return (
    <div className="App" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'flex-start' }}>
      <div className="cards-container" style={{ flexGrow: 1, width: '300px' }}>
        {articles.map((article, index) => (
          <div key={index} onClick={() => setSelectedArticle(article)}>
            <Card
              title={article.title}
              content={article.content}
              readTime={article.time}
              ImageSrc={article.title_image}
            />
          </div>
        ))}
      </div>

      <div style={{ margin: '1rem', padding: '1rem', marginTop: '0px' }}>
        <TrendingSidebar />
      </div>
    </div>
  );
};

export default SecondPage;
