import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../css/homePage.css"; 
import LeftPanel from "../Componets/SideBar";
import CardContainer from "../Componets/CardContainer";
import ImageCardContainer from "../Componets/ImageCardContainer";
import TrendingSidebar from "../Componets/ Containers/TrendingItem";
import ArticlePage from "../Componets/Other/NewsAriticalPage";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';



function App() {
  const [news, setNews] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content", {
          method: "GET",
          headers: {
            "Cache-Control": "no-cache", 
          },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNews(data); 
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  console.log("this is news", news);

  function handleNext() {
    setCurrentIndex((prevIndex) => Math.min(prevIndex + 1, news.length - 1));
  }

  function handlePrevious() {
    setCurrentIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  }

  function renderArticles() {
    const startIndex = currentIndex;
    const endIndex = Math.min(currentIndex + 3, news.length);
    const articlesToShow = news.slice(startIndex, endIndex);
    return articlesToShow.map((article, index) => (
      <a
        key={index}
        href={`/article/${startIndex + index}`}
        target="_blank"
        rel="noopener noreferrer"
        style={{ textDecoration: "none" }} 
      >
        <Article
          key={index}
          title={article.title}
          paragraph_text={article.paragraphs[0].paragraph_text}
          time={article.time}
          src={article.images[0].image_url}
        />
      </a>
    ));
  }

  console.log("newdata", news);

  function Article({ title, paragraph_text, time, src }) {
    console.log("newdata", news);
    return (
      <div className="article-card">
        <img
          src={src}
          alt="Article Thumbnail"
          className="article-image"
        />
        <div className="article-content">
          <h3>{title}</h3>
          <p>{paragraph_text}</p>
          <div className="read-time">{time} mins read</div>
        </div>
      </div>
    );
  }

  // Define articleData here
  const articleData = {
    title: "Sample Article Title",
    content: [
      {
        type: "paragraph",
        text: "This is the first paragraph of the article.",
      },
      { type: "image", src: "path_to_image_1.jpg", alt: "Image description" },
      {
        type: "paragraph",
        text: "This is the second paragraph of the article.",
      },
      {
        type: "image",
        src: "path_to_image_2.jpg",
        alt: "Another image description",
      },

    ],
  };

  return (
    <div className="layout-container">
      <aside className="left-panel">
        <LeftPanel />
      </aside>
      <main className="main-content">
        <div style={{ display: "flex", flexDirection: "row" }}>
          <div
            style={{
              minWidth: "300px", 
              background: "transparent",
              height: "680px", 
            }}
          >
            <TrendingSidebar />
          </div>
          <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "stretch",
                width: "100%",
                margin: "0 auto",
                boxSizing: "border-box",
              }}
            >
              <CardContainer />
            </div>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "stretch",
                width: "100%",
                margin: "0 auto",
                boxSizing: "border-box",
              }}
            >
              <ImageCardContainer />
            </div>
          </div>
        </div>
        <div className="section-container">
          <h2 className="section-header">Articles</h2>
          <div className="articles-container">
            {renderArticles()}
          </div>
          <div>
            <button onClick={handlePrevious}>Previous</button>
            <button onClick={handleNext}>Next</button>
          </div>
          <div className="articles-container">
            {renderArticles()}
          </div>
          <div>
            <button onClick={handlePrevious}>Previous</button>
            <button onClick={handleNext}>Next</button>
          </div>
        </div>
        <div>
          <ArticlePage
            title={articleData.title}
            content={articleData.content}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
