import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  background-color: transparent;
  color: white;
  padding: 20px;
  margin: 10px;
  border-radius: 10px;
  box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
  width: 330px;
`;

const Tab = styled.div`
  display: inline-block;
  background-color: ${props => props.active ? 'green' : 'transparent'};
  padding: 10px;
  border-radius: 10px 10px 0 0;
  margin-right: 5px;
  cursor: pointer;
  margin-top: 15px;
`;

const Title = styled.h1`
  font-size: 18px;
  font-weight: bold;
`;

const Text = styled.p`
  font-size: 14px;
`;
const flexContainerStyle = {
  display: 'flex', // Enable flex layout
  flexWrap: 'wrap', // Allow items to wrap to the next line if space is insufficient
  alignItems: 'center', // Align items vertically in the center
  gap: '10px', // Optional: add some space between the image and the title
};
const Card = ({ title, content, onClick }) => {
  const [activeTab, setActiveTab] = React.useState('topic1');

  return (
    <CardContainer onClick={onClick}>
      <div style={{height:'200px',backgroundColor:'whitesmoke',borderRadius:'5px'}}></div>
      <div>
        <Tab active={activeTab === 'topic1'} onClick={() => setActiveTab('topic1')}>Topic 1</Tab>
        <Tab active={activeTab === 'topic2'} onClick={() => setActiveTab('topic2')}>Topic 2</Tab>
      </div>
      <Title>{title}</Title>
      <Text>{content}</Text>
    </CardContainer>
  );
};

const ArticleMobaileScreen = () => {
  const [activeCard, setActiveCard] = useState(null);
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await fetch('http://localhost:8000/articles');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setArticles(data);
      } catch (error) {
        console.error('There was a problem with your fetch operation:', error);
      }
    };

    fetchArticles();
  }, []);
console.log('this is mobaile data',articles)
  const renderActiveCard = () => (
    <div style={{ padding: '20px', color: 'white' }}>
     <div style={flexContainerStyle}>
      <img src="https://picsum.photos/seed/picsum/200/300" alt="" width={50} height={30} />
      <h1>{activeCard.title}</h1>
    </div>
      <p>{activeCard.p1}</p>
      <img src="https://picsum.photos/seed/picsum/200/300" width={350} height={300}/>
      <p>{activeCard.p2}</p>
      <p>{activeCard.p3}</p>
      <img src="https://picsum.photos/seed/picsum/200/300" width={350} height={300}/>
      <p>{activeCard.p4}</p>
      <p>{activeCard.p5}</p>
      <img src="https://picsum.photos/seed/picsum/200/300" width={350} height={300}/>
      <p>{activeCard.p6}</p>
      <p>{activeCard.p7}</p>
      <img src="https://picsum.photos/seed/picsum/200/300" width={350} height={300}/>
      <p>{activeCard.p8}</p>
      <p>{activeCard.p9}</p>
      <p>{activeCard.p10}</p>
      {/* Here, you can add more details or actions related to the active card */}
    </div>
  );

  return (
    <div style={{ display: 'flex', flexDirection: 'column', justifyItems: 'center', overflowX: 'scroll' }}>
      {activeCard == null ? (
        articles.map((article, index) => (
          <Card
            key={index}
            title={article.title}
            content={article.p1} // Assuming 'p1' is a property of your article objects
            onClick={() => setActiveCard(article)}
          />
        ))
      ) : renderActiveCard()}
    </div>
  );
};

export default ArticleMobaileScreen;
