// Import React, useEffect, useState, Suspense for lazy loading
import React, { useEffect, useState, Suspense } from "react";
import { Route, BrowserRouter, Routes } from "react-router-dom";
import Header from "./components/Header";
import "./App.css";
import { RingLoader } from "react-spinners";

// Lazy load the components
const HomePage = React.lazy(() => import("./pages/HomePage"));


function App() {
  const [isAuth, setIsAuth] = useState(getCookie("token") ? true : false);
  // const [userCredits, setUserCredits] = useState(null);\
  const [userCredits, setUserCredits] = useState(() => {
    const storedCredits = localStorage.getItem("userCredits");
    return storedCredits ? parseInt(storedCredits, 10) : null;
  });

  const [errorMessage, setErrorMessage] = useState("");
  const [sessionChats, setSessionChats] = useState(null);
  const [sessionIds, setSessionIds] = useState(null);







  // Check if the current route is /subscription
  const isSubscriptionRoute =
    window.location.pathname.includes("/subscription");

  return (
    <div className="App">

      <BrowserRouter>
        <Header
          isAuth={isAuth}
          userCredits={userCredits}
          setIsAuth={setIsAuth}
        />

        <Suspense
          fallback={
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                height: "75vh",
              }}
            >
              <RingLoader color="#6cd97e" size={100} />
            </div>
          }
        >
          {" "}
          <Routes>
            <Route path="/HomePage" element={<HomePage />}>
              <Route path="/HomePage" element={<HomePage />} />
            </Route>
          </Routes>
        </Suspense>
      </BrowserRouter>
    </div>
  );
}

export default App;
