import Card from './Card'; // Assuming Card is in the same directory

const CardContainer = () => {
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'stretch',
      width: '100%',
      margin: '0 auto',
      boxSizing: 'border-box',
    }}>
      <div style={{width:'35%'}}>
      <div style={{
      flex: 1,
      margin: '10px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
      borderRadius: '10px',
      background: '#fff',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '300px', // Adjust based on content
    }}>
      {/* <p>{text}</p> */}
    </div>
      </div>
      <Card text="Card 3 Content" />
    </div>
  );
};

export default CardContainer;
