import React, { useState, useEffect } from 'react';
import "../../css/MobaileScreenCard.css"; // Ensure this CSS file exists and styles your cards appropriately

const Card = ({ imageSrc, title, content, time }) => {
  return (
    <div className="card">
      <img src={imageSrc} alt="Card" className="card-image" />
      <div className="card-content">
        <h1 className="card-title">{title}</h1>
        <p className="card-text">{content}</p>
        <div className="card-footer">
          <span className="time">{time}</span>
        </div>
      </div>
    </div>
  );
};

const NewsMobileCard = () => {
  const [news, setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content", {
          method: "GET",
          headers: {
            "Cache-Control": "no-cache",
          },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNews(data);
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  // Assuming your news data structure matches the example given,
  // and that each news item includes a title, paragraphs (for content),
  // and images array (we'll use the first image as the card image)
  return (
    <div className="card-container">
      {news.map((item, index) => (
        <Card
          key={index}
          imageSrc={item.images?.[0]?.image_url || 'https://via.placeholder.com/150'} // Fallback to a placeholder if no image
          title={item.title}
          content={item.paragraphs?.[0]?.paragraph_text || 'No content available.'} // Use the first paragraph as content
          time="Just now" // Placeholder time, adjust as necessary
        />
      ))}
    </div>
  );
}

export default NewsMobileCard;
