import React,{useState,useEffect} from "react";
import PropTypes from "prop-types";

const ArticlePage = ({ title, titleImage, paragraphs, images }) => {
  // Combine and sort paragraphs and images based on their order
  const contentItems = [...paragraphs, ...images.map(image => ({...image, text: '', type: 'image'}))]
    .sort((a, b) => a.order - b.order);

  return (
    <article style={styles.articleContainer}>
      <h1 style={styles.heading}>{title}</h1>
      {titleImage && (
        <img
          style={styles.image}
          src={titleImage.image_url}
          alt="Title visual representation"
        />
      )}
      {contentItems.map((item, index) => {
        if (item.paragraph_text) {
          return (
            <p key={item._id} style={styles.paragraph}>
              {item.paragraph_text}
            </p>
          );
        } else if (item.image_url) {
          return (
            <img
              key={item._id}
              style={styles.image}
              src={item.image_url}
              alt={`Image description ${index + 1}`}
            />
          );
        } else {
          return null;
        }
      })}
    </article>
  );
};

// Styles remain the same as your initial setup
const styles = {
  // Your styles here
};

ArticlePage.propTypes = {
  title: PropTypes.string.isRequired,
  titleImage: PropTypes.shape({
    image_url: PropTypes.string.isRequired,
    image_type: PropTypes.string,
    _id: PropTypes.string.isRequired,
  }),
  paragraphs: PropTypes.arrayOf(
    PropTypes.shape({
      paragraph_text: PropTypes.string.isRequired,
      order: PropTypes.number.isRequired,
      _id: PropTypes.string.isRequired,
    })
  ).isRequired,
  images: PropTypes.arrayOf(
    PropTypes.shape({
      image_url: PropTypes.string.isRequired,
      image_type: PropTypes.string,
      order: PropTypes.number.isRequired,
      _id: PropTypes.string.isRequired,
    })
  ).isRequired,
};

// Example usage
const ArticlePageRoute = () => {
  const [news, setNews] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch("http://localhost:8000/content", {
          method: "GET",
          headers: {
            "Cache-Control": "no-cache", 
          },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setNews(data); 
      } catch (error) {
        console.error("There was a problem with your fetch operation:", error);
      }
    };

    fetchNews();
  }, []);

  console.log("this is news", news);
  const articleData = {
    title: "Google launches Gemini 1.5 with ‘experimental’ 1M token context",
    titleImage: { image_url: "https://picsum.photos/200", _id: "title_image_id" },
    paragraphs: [
      // Your paragraphs data here
    ],
    images: [
      // Your images data here
    ],
  };

  const art = news[0];
  console.log(art);

  return (
    <div>
      <ArticlePage
        title={articleData.title}
        titleImage={articleData.titleImage}
        paragraphs={articleData.paragraphs}
        images={articleData.images}
      />
    </div>
  );
};

export default ArticlePageRoute;
