import React from 'react';
import "../../css/MobaileScreenCard.css" // Make sure to create this CSS file

const Card = ({ imageSrc, title, content, time }) => {
  return (
    <div className="card">
      <img src={imageSrc} alt="Card" className="card-image"/>
      <div className="card-content">
        <h1 className="card-title">{title}</h1>
        <p className="card-text">{content}</p>
        <div className="card-footer">
          <span className="time">{time}</span>
        </div>
      </div>
    </div>
  );
};




const NewsMobaileCard = ({ cards }) => {
    return (
      <div className="card-container">
        {cards.map((card, index) => (
          <Card key={index} {...card} />
        ))}
      </div>
    );
  }

  export default NewsMobaileCard;
