import React,{useState,useEffect} from 'react';
import '../css/homePage.css'; // Make sure this path includes both CSS if they are separate or ensure the combined CSS is correctly referenced
import LeftPanel from '../Componets/SideBar';
import CardContainer from '../Componets/CardContainer';
import ImageCardContainer from '../Componets/ImageCardContainer';
import TrendingSidebar from '../Componets/ Containers/TrendingItem';
import Image from '../assets/Images/FeaturedModels/block-5.png'
import ArticlePage from "../Componets/Other/NewsAriticalPage"

// Simplified App component
function App() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    async function fetchNews() {
      try {
        const response = await fetch('http://localhost:8000/content');
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.json();
        setNews(data);
      } catch (error) {
        console.error('Fetch error:', error);
      }
    }
    fetchNews();
  }, []);

  // Log news data
  console.log('News data:', news);

  // Article data for demonstration
  const articleData = {
    title: 'Sample Article Title',
    content: [
      // Assuming this structure for demonstration
      { type: 'paragraph', text: 'This is the first paragraph.' },
      { type: 'image', src: 'path_to_image.jpg', alt: 'Image description' },
      // Additional content as needed
    ],
  };

  return (
    <div className="layout-container">
      <LeftPanel />
      <MainContent articleData={articleData} />
    </div>
  );
}

// Extracted MainContent for clarity
function MainContent({ articleData }) {
  return (
    <main className="main-content">
      <ContentLayout>
        <TrendingSidebar />
        <CardSection />
        <ImageCardSection />
      </ContentLayout>
      <ArticlePage {...articleData} />
    </main>
  );
}

// Simplified layout components for Cards and ImageCards
function ContentLayout({ children }) {
  return <div style={{ display: 'flex', flexDirection: 'row' }}>{children}</div>;
}

function CardSection() {
  return (
    <div className="card-section">
      <CardContainer />
    </div>
  );
}

function ImageCardSection() {
  return (
    <div className="image-card-section">
      <ImageCardContainer />
    </div>
  );
}

export default App;
