import React from 'react';
import Card from '../Componets/NextPage/NewsCard';
import TrendingSidebar from '../Componets/ Containers/TrendingItem';
import ArticlePage from '../Componets/Other/NewsAriticalPage';

const SecondPage = () => {
  // This data can be dynamic, fetched from an API, etc.
  const cardData = {
    title: "Lorem ipsum dolor sit amet consectetur.",
    content: "Lorem ipsum dolor sit amet consectetur. Nam magna habitant sed odio id. Nisl a eu vitae amet amet. Aliquam tellus id et at mauris eget eget. Nisl congue praesent viverra et.",
    readTime: "2",
  };

  const articleData = {
    title: 'Sample Article Title',
    content: [
      { type: 'paragraph', text: 'This is the first paragraph of the article.' },
      { type: 'image', src: 'path_to_image_1.jpg', alt: 'Image description' },
      { type: 'paragraph', text: 'This is the second paragraph of the article.' },
      { type:
  'image', src: 'path_to_image_2.jpg', alt: 'Another image description' },
  // ... additional paragraphs and images
  ],
  codeSnippet: `
  // Example JavaScript Code
  function greet(message) {
  console.log(message);
  }

  scss
  Copy code
  greet('Hello, World!');
  `
  };

  return (
    <>
    <div className="App" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'flex-start' }}>
      <div className="cards-container" style={{ flexGrow: 1,width:'300px' }}>
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
        <Card title={cardData.title} content={cardData.content} readTime={cardData.readTime} />
      </div>
      <div style={{ margin:'1rem',padding:'1rem',marginTop:'0px'}}> {/* Adjust the sidebar width as needed */}
        <TrendingSidebar />
      </div>
    </div>
    <div>
<ArticlePage
     title={articleData.title}
     content={articleData.content}
     codeSnippet={articleData.codeSnippet}
   />
</div>
    </>
  );
};

export default SecondPage;
