import React from 'react';

// Main contact component
const App = () => {
  const iconStyle = {
    width: '24px', // Replace with the actual size of your icons
    height: '24px', // Replace with the actual size of your icons
    marginRight: '10px',
  };

  const sectionStyle = {
    backgroundColor: '#ffffff',
    padding: '20px',
    borderRadius: '5px',
    marginBottom: '20px',
  };

  const contactInfoStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '10px',
  };

  const textStyle = {
    color: '#333333',
    marginBottom: '20px',
    lineHeight: '1.5',
  };

  const titleStyle = {
    fontSize: '18px',
    color: '#333333',
    marginBottom: '10px',
  };

  const commonTextStyle = {
    fontSize: '16px',
    color: '#333333',
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', maxWidth: '768px', margin: 'auto', backgroundColor: '#f4f4f4', padding: '20px' }}>
      <div style={textStyle}>
        We are here to help! MyTravelway has extended support and communication round the clock on business days. You may reach out to us at your convenience. We keep a record of all the communication for quality, training, and research purposes and to serve you in a better way. Following is the contact information to address your queries. Business Days: Monday to Saturday, Timing: 8:00AM to 11:00PM, Sunday- Week-off and Holidays
      </div>

      <div style={sectionStyle}>
        <h2 style={titleStyle}>Reservations Centre</h2>
        <div style={contactInfoStyle}>
          <img src="phone-icon.png" alt="Phone" style={iconStyle} />
          <span style={commonTextStyle}>+1 (800) 123-4567</span>
        </div>
        <div style={contactInfoStyle}>
          <img src="email-icon.png" alt="Email" style={iconStyle} />
          <span style={commonTextStyle}>info@splendid.com</span>
        </div>
      </div>

      <div style={sectionStyle}>
        <h2 style={titleStyle}>Hotel Business Centre</h2>
        <div style={contactInfoStyle}>
          <img src="email-icon.png" alt="Email" style={iconStyle} />
          <span style={commonTextStyle}>partnerships@splendid.com</span>
        </div>
      </div>

      <div style={sectionStyle}>
        <h2 style={titleStyle}>Media and press</h2>
        <div style={contactInfoStyle}>
          <img src="email-icon.png" alt="Email" style={iconStyle} />
          <span style={commonTextStyle}>press@splendid.com</span>
        </div>
      </div>

      <div style={sectionStyle}>
        <h2 style={titleStyle}>Feedback</h2>
        <div style={contactInfoStyle}>
          <img src="email-icon.png" alt="Email" style={iconStyle} />
          <span style={commonTextStyle}>feedback@splendid.com</span>
        </div>
      </div>

      {/* Add Follow us section with icons */}

      {/* Add FAQ section with arrow icon or as needed */}

      <div style={textStyle}>
        <h2 style={titleStyle}>Postal/Mail</h2>
        <p style={commonTextStyle}>171, Panduranga Nagar, Off. B.G. road, Bengaluru-560076</p>
      </div>
    </div>
  );
};

export default App;
